import React, { useState, useEffect } from 'react'
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import SideNav from '../../Components/SideNav'
import Typography from '@mui/material/Typography';
import '../Single_user/single_user.scss'
import Order_table from '../../Components/Order_table';
const drawerWidth = 250;
export default function Single_user() {
    const [order, setOrder] = useState([])
    const [second, setSecond] = useState([])


    useEffect(() => {
        setOrder(["Order_Id", "Status", "Amount", "User", "Product", "Transction_id", "Date", "Action"])

        setSecond([{
            Order_id: 1,
            Status: 'Delivered',
            Amount: 500,

            User_name: 'Amit kumar yadav',
            Product: '4',

            Transction_Id: 'dfg542314tfd',
            Date: '02/01/2002',


        }, {
            Order_id: 2,
            Status: 'Pending',
            Amount: 500,

            User_name: 'Atul yadav',
            Product: '3',

            Transction_Id: 'dfg542314tfd',
            Date: '02/01/2002',


        }, {
            Order_id: 3,
            Status: 'proceeding',
            Amount: 500,

            User_name: 'harsh kumar',
            Product: '4',

            Transction_Id: 'dfg542314tfd',
            Date: '02/01/2002',


        }, {
            Order_id: 4,
            Status: 'Delivered',
            Amount: 500,

            User_name: 'Nafisul hasan',
            Product: '4',

            Transction_Id: 'dfg542314tfd',
            Date: '02/01/2002',


        }
        ])
    }, [])
    return (
        <>
            <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                <SideNav />

                <Toolbar sx={{
                    width: { md: `calc(100% - ${drawerWidth}px)` },
                    ml: { md: `${drawerWidth}px` }
                }} />
                <Box sx={{
                    width: { md: `calc(100% - ${drawerWidth}px)` },
                    ml: { md: `${drawerWidth}px` }
                }} >


                    <div className='Single_user_container'>

                        <div className='row m-0 p-0 my-4'>
                            <div className='col-sm-3 col-md-5 border d-flex flex-column justify-content-around'>
                                <div>
                                    <img src='https://cdn-icons-png.flaticon.com/512/3135/3135715.png' alt='https://cdn-icons-png.flaticon.com/512/3135/3135715.png' style={{ height: '100%', width: '100%' }} />
                                </div>
                                <div>
                                    <p><span>Name : </span><span>Amit kumar yadav</span></p>
                                    <p><span>Email: </span><span>yaduvanshiamit545@gmail.com</span></p>
                                    <p><span>Mobile: </span><span>6388131984</span></p>
                                    <p><span>Gender: </span><span>Male</span></p>
                                    <p><span>Address: </span><span>122/7 Atrauli lucknow uttar pradesh </span></p>
                                </div>
                            </div>
                            <div className='col-sm-9 col-md- border'>
                                <h3>Total_order</h3>
                                <Order_table Thead={order} Tbody={second} />
                            </div>
                        </div>
                    </div>



                </Box>


            </Box>
        </>
    )
}
